import {useState} from "react"
import { useNavigate } from "react-router-dom";
import './login.css';

 function Login(){
   const [userName,setUserName]=useState("");
   const [password,setPassword]=useState("");
   const [isSubmitFocused, setIsSubmitFocused] = useState(false);
   const navigate=useNavigate(); 


   const handleFocus = () => {
    setIsSubmitFocused(true);
  };

  const handleBlur = () => {
    setIsSubmitFocused(false);
  };


   const changeUserName= (event)=>{
    setUserName(event.target.value);
   }
   const changePassword= (event)=>{
    setPassword(event.target.value);
   }

  
   const checkUser= (event)=>{
    debugger;
    const url ="http://localhost:3000/login";
    const requestLonig={
     method:"POST",
     headers:{
       "Content-Type":"application/json",
     },
     body:JSON.stringify({userName,password}),
    }; 
        event.preventDefault();
        fetch(url,requestLonig)
        .then((response) => {
          if(response.status===200){
            return response.json() 
          }
          else if(response.status===404){
            throw ("The password or the user name aren't correct")
          }
        })
        .then((data)=>data[0])
        .then((user)=>{
          var pass=user.address.geo.lat.slice(-4);
          if(pass==password){
            localStorage.setItem("user", JSON.stringify(user));
            navigate(`/users/${user.id}/home`)
          }
          else{
            alert("the password is incorrect")
            setPassword("");
          }
        }).catch((error)=>{
          setPassword("");
          setUserName("");
          alert(error)})  
  }
  

  
   return (
    <div className="centered-container">
    <form className="formSubmit"  onSubmit={checkUser}>
      <label>User name:</label>
      <input
      required
      value={userName}
      onChange={changeUserName}
      placeholder="name"
      /><br/>
      <label>Password:</label>
      <input
      required
      value={password}
      onChange={changePassword}
      placeholder="password"
      type="password"
      /><br/>
        <input 
        type="submit"
         value="Submit"
         className={isSubmitFocused ? "bold-on-submit" : ""}
         onFocus={handleFocus}
         onBlur={handleBlur}
         />
    </form>
    </div>
   )
}
export default Login;
