 import React, {useState,useEffect} from "react";
 import { Link } from "react-router-dom";
function Posts(){
    var jsonUser=localStorage.getItem("user");
    var user=JSON.parse(jsonUser);
    var userId=user.id;
    const [posts,setPosts]=useState([]);
    const[findPosts,setFindPosots]=useState(true)
    useEffect(()=>
    {
        fetch(`https://jsonplaceholder.typicode.com/users/posts?userId=${userId}`)
        .then((response) => response.json())
        .then((posts)=> setPosts(posts))
        .catch(()=>setFindPosots(false));

    },[]);
    if(findPosts){
     return (
        
        posts.map((post)=><Link >{post.title}</Link>)
        
     );
    }
    return (
        <h2>there isn't posts</h2>
    );
}
export default Posts;
