 import React, {useState,useEffect} from "react";
 import { Link } from "react-router-dom";
function Posts(){
    let jsonUser=localStorage.getItem("user");
    let user=JSON.parse(jsonUser);
    let userid=user.id;

    const [posts,setPosts]=useState([]);
    const[findPosts,setFindPosots]=useState(true)
    useEffect(()=>
    {
        fetch(`https://jsonplaceholder.typicode.com/users/posts?userId=${userid}`)
        .then((response) => response.json())
        .then((posts)=> setPosts(posts))
        .catch(()=>setFindPosots(false));

    },[]);
    if(findPosts){

        let postsHtml=posts.map(post=>(<h1>{post.title}</h1>));
     return (
        <div>
            {postsHtml}
        </div>
        );
    }
    return (
        <h2>there isn't posts</h2>
    );
}
export default Posts;
