import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

function Posts() {
  let jsonUser = localStorage.getItem("user");
  let user = JSON.parse(jsonUser);
  let userid = user.id;

  const [posts, setPosts] = useState([]);
  const [findPosts, setFindPosts] = useState(true);
  const [currentPost,setCurrentPost]=useState(null);
  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts?userId=${userid}`)
      .then((response) => response.json())
      .then((data) => setPosts(data))
      .catch(() => setFindPosts(false));
  }, [userid]);

  selectedPost = (postId) =>{
    setCurrentPost(postId)
  };

  if (findPosts) {
    let postsHtml = posts.map((post) =>{
    <div key={post.id}>
     <button key={post.id} onClick={selectedPost(post.id)}>
     <h3>{post.title}</h3>
     <p>{post.body}</p>
     </button>
     {/* </div>isRefresh:{visibility: 'hidden'} */}
     <button style={{ visibility: post.id === currentPost ? 'visible' : 'hidden' }}> 
        comments
     </button>
    </div>
  });
    return (
      <div>
        {postsHtml}
      </div>
    );
  }

  return (
    <h2>There are no posts</h2>
  );
}

export default Posts;
